INSERT INTO `wp_posts` VALUES (202,1,'2014-08-26 12:11:16','2014-08-26 12:11:16','[one_third]Centered Yoga Studio is located on the outskirts of Stroud in the picturesque Slad Valley. It is housed in a renovated 17th Century cowshed within the walled garden and grounds of a Georgian manor house.\r\n\r\nThe clean, bright and heated practice room is fully equipped with yoga mats and all the props necessary for the <a href=\"#iyengar-yoga\">Iyengar method</a>.\r\n\r\nWith separate male and female changing rooms and WC, the studio makes a comfortable, dedicated setting for the practice of Iyengar Yoga. [/one_third]  [two_third][gallery link=\"file\" ids=\"146,113,114,115,116,117\"][/two_third]','The Studio','','inherit','closed','closed','','10-revision-v1','','','2014-08-26 12:11:16','2014-08-26 12:11:16','',10,'http://centered.co.uk/10-revision-v1/',0,'revision','',0);
