INSERT INTO `wp_posts` VALUES (224,1,'2014-08-26 14:17:14','2014-08-26 14:17:14','[three_fourth] Ian Hibbs is a fully qualified Iyengar Yoga teacher to Introductory Level 2 Certification, able to teach newcomers to Yoga and also those who wish to progress in their established practice.\r\n\r\nHe regularly travels to India to continue his studies and practice with senior teachers from the Iyengar Institute. He has also had the privilege to have been taught by BKS Iyengar and his daughter, Geeta Iyengar.\r\n[gallery columns=\"4\" link=\"file\" ids=\"194,189,196,190,195,192,191,193\"]\r\n[/three_fourth][one_fourth]<ul class=\"right-nav\">\r\n	<li class=\"current\"><a href=\"/the-teacher\">The Teacher</a></li>\r\n	<li><a href=\"/preparation\">Preparation</a></li>\r\n	<li><a href=\"/timetable\">Timetable</a></li>\r\n	<li><a href=\"/cost\">Cost</a></li>\r\n</ul>\r\n[/one_fourth]','The Teacher','','inherit','closed','closed','','182-revision-v1','','','2014-08-26 14:17:14','2014-08-26 14:17:14','',182,'http://centered.co.uk/182-revision-v1/',0,'revision','',0);
